import React, {Component} from "react"

class ProjectForm extends Component{

    state = {
        projectName: "",
        projectDescription: ""
    }

    handleChangeName = event => {
        this.setState({
            projectName: event.target.value
        })
    }

    handleChangeDescription = event => {
        this.setState({
            projectDescription: event.target.value
        })
    }

    saveProject = () => {
        var info = {projectId: 0, projectName: this.state.projectName, projectDescription: this.state.projectDescription}
        this.props.saveProject(info)
    }


    render(){
        return(
            <div className="projectform">
                <input type="text" value={this.state.projectName} onChange={this.handleChangeName} />
                <textarea value={this.state.projectDescription} onChange={this.handleChangeDescription} cols="30" rows="10"></textarea>
                <div className="cancelsave">
                    <button onClick={this.props.cancelProject}>Cancel</button>
                    <button onClick={this.saveProject}>Save</button>
                </div>
            </div>
        );
    }
}


export default ProjectForm
