class Column extends Component {

    state = {
        projects: [
            1,
            1
        ]
    }

    removeProject = () => {
        const p = this.state.projects.slice(0,-1)
        this.setState({
            projects: p
        })
    }


    addProjects = () => {
        this.setState({
            projects: [...this.state.projects, 1]
        })
    }

    render(){
        return (
            <div className="column">
                <div>
                    <h1>{props.name}</h1>
                    <button onClick={this.addProjects}>Create</button>
                </div>
                {this.state.projects.map(project => <Project removeProject={this.removeProject}/>)}
            </div>
        );
    }

}



export default Column
