import Project from "./project"
import ProjectForm from "./projectForm"
import React, {Component} from "react"

class Column extends Component {

    constructor(props){
        super(props)

        this.fetchProjects()

        this.state = {
            projects: [{projectId: 1, projectName: "TestProject", projectDescription: "this is a test"},
                        {projectId: 2, projectName: "KKKKKK", projectDescription: "this is a test"}],
            inputBox: false
        }
    }


    removeProject = (id) => {
        console.log(id)
        const newProjectList = this.state.projects.filter((item) => item.projectId !== id );
        this.setState({
            projects: newProjectList
        })
    }


    addProjects = () => {
        this.setState({
            inputBox: true
        })
        console.log(this.state.inputBox)
        //her må vi poste til backend
    }

    saveProject = (newProject) => {

        this.setState({
            projects: [...this.state.projects, newProject],
            inputBox: false
        })
    }

    cancelProject = () => {
        this.setState({
            inputBox: false
        })
    }

    render(){
        console.log("rendereing")
        return (
            <div className="column">
                <div>
                    <h1>{this.props.name}</h1>
                    {this.props.id == "backlog" &&
                    <button onClick={this.addProjects}>Create</button>
                    }
                </div>

                {this.props.id == "backlog" && this.state.projects.map(project => <Project id={project.projectId} name={project.projectName} description={project.projectDescription} removeProject={this.removeProject}/>)}
                {this.state.inputBox && <ProjectForm saveProject={this.saveProject} cancelProject={this.cancelProject}/>}
            </div>
        );
    }

    fetchProjects = () => {
        fetch("http://localhost:5000/api/projects", {mode: "no-cors"})
            .then(response => response.json())
            .then(data => console.log(data))
    }


}



export default Column
