import Project from "./project"
import ProjectForm from "./projectForm"
import React, {Component} from "react"

class Column extends Component {

    state = {
        projects: [{projectId: 0, projectName: "TestProject", projectDesciption: "this is a test"}],
        inputBox: false
    }

    removeProject = () => {
        const p = this.state.projects.slice(0,-1)
        this.setState({
            projects: p
        })
    }


    addProjects = () => {
        this.setState({
            projects: [...this.state.projects, 1],
            inputBox: true
        })
        console.log(this.state.inputBox)
    }

    render(){
        return (
            <div className="column">
                {this.props.id == "backlog" ? (console.log(1)):(console.log(0))}

                <div>
                    <h1>{this.props.name}</h1>
                    {this.props.id == "backlog" &&
                    <button onClick={this.addProjects}>Create</button>
                    }
                </div>

                {this.props.id == "backlog" && this.state.projects.map(project => <Project name={project.projectName} description={project.projectDesciption} removeProject={this.removeProject}/>)}
                {this.state.inputBox && <ProjectForm/>}
            </div>
        );
    }

}



export default Column
