import Column from "./column"
import React, {Component} from "react"


//muligen ha alle projects state her og hvilken colonne de hører til, slik at jeg kan pase ned riktig


class ProjectBoard extends Component {

    state = {
        backlogProjects: [],
        inProgressProjects: [],
        doneProjects: [],
        inputBox: false
    }


    async componentDidMount() {

        var arr = []

        console.log("inif")
        var data = await this.fetchProjects()
        console.log(data[0].id)
        data.map(d => {
            const project = {projectId: d.id, projectName: d.name, projectDescription: d.description}
            arr.push(project)
        })
        console.log(arr)


        this.setState({backlogProjects: [...this.state.backlogProjects, ...arr]})

    }



    //må bruke DELETE fetch
    async removeProjectReal(id, column) {
        if (column == "backlog"){
            const response = await this.deleteProject(id)
            console.log(response)
            const newProjectList = this.state.backlogProjects.filter((item) => item.projectId !== id );
            this.setState({
                backlogProjects: newProjectList
            })
        }

    }


    addProjects = () => {
        this.setState({
            inputBox: true
        })
        console.log(this.state.inputBox)
    }


    //her må vi poste til backend
    async saveProject(newProject) {
        const body = { Name: newProject.projectName, Description: newProject.projectDescription}
        const data = await this.postProject(body)
        console.log(data)
        //need to have data on right format
        const project = {projectId: data.id, projectName: data.name, projectDescription: data.description}
        this.setState({
            backlogProjects: [...this.state.backlogProjects, project],
            inputBox: false
    })
    }

    cancelProject = () => {
        this.setState({
            inputBox: false
        })
    }

    moveToInProgress = (id, ) => {

    }



    render(){
        return(
            <div className="projectboard">
                <Column id="done" name="Done"
                    inputBox={this.state.inputBox}
                    projects={this.state.doneProjects}
                    removeProjectReal={this.removeProjectReal.bind(this)}
                    addProjects={this.addProjects}
                    saveProject={this.saveProject.bind(this)}
                    cancelProject={this.cancelProject}/>

                <Column id="in-progress" name="In Progress"
                    inputBox={this.state.inputBox}
                    projects={this.state.inProgressProjects}
                    removeProjectReal={this.removeProjectReal.bind(this)}
                    addProjects={this.addProjects}
                    saveProject={this.saveProject.bind(this)}
                    cancelProject={this.cancelProject}/>

                <Column id="backlog" name="Backlog"
                    inputBox={this.state.inputBox}
                    projects={this.state.backlogProjects}
                    removeProjectReal={this.removeProjectReal.bind(this)}
                    addProjects={this.addProjects}
                    saveProject={this.saveProject.bind(this)}
                    cancelProject={this.cancelProject}/>
            </div> 
        );
    }




    async fetchProjects() {
        console.log("in fetch")
        const response = await fetch('http://localhost:5000/api/projects');
        return response.json();

    }

    async postProject(body){
        console.log("in save")
        const response = await fetch('http://localhost:5000/api/projects', {
            method: 'POST',
            mode: 'cors',
            headers: {
                'Content-Type': 'application/json'
              },
            body: JSON.stringify(body)
        })
        return response.json()

    }

    async deleteProject(id){
        const response = await fetch(`http://localhost:5000/api/projects/${id}`, {
            method: 'DELETE',
            mode: 'cors',
        })
        return response
    }




}



export default ProjectBoard
